// n8n Code Node - Parse SSL Scanner Output
// This code processes the SSH node output and formats it for Discord notifications

const results = [];

for (const item of $input.all()) {
  try {
    // Extract the stdout from SSH response
    const sshOutput = item.json;

    // Check if SSH command was successful
    if (sshOutput.code !== 0) {
      results.push({
        json: {
          hostname: 'unknown',
          status: 'error',
          error: sshOutput.stderr || 'SSH command failed',
          alert: true,
          alertLevel: 'critical'
        }
      });
      continue;
    }

    // Parse the JSON output from stdout
    let sslData;
    try {
      sslData = JSON.parse(sshOutput.stdout);
    } catch (parseError) {
      results.push({
        json: {
          hostname: 'unknown',
          status: 'error',
          error: 'Failed to parse SSL scanner output',
          alert: true,
          alertLevel: 'critical'
        }
      });
      continue;
    }

    // Extract key information for Discord notification
    const parsedResult = {
      // Basic info
      hostname: sslData.hostname,
      port: sslData.port || 443,
      scanTime: sslData.scanTime,
      scanDuration: sslData.scanDuration,

      // Overall status
      overallGrade: sslData.overallGrade,
      connectionStatus: sslData.connectionStatus || 'unknown',
      success: sslData.success || false,

      // Certificate info
      certificate: {
        subject: sslData.certificate?.subject || 'unknown',
        issuer: sslData.certificate?.issuer || 'unknown',
        validUntil: sslData.certificate?.validUntil,
        daysUntilExpiry: sslData.certificate?.daysUntilExpiry,
        isValid: sslData.certificate?.isValid || false,
        issues: sslData.certificate?.issues || [],
        hostnameMatch: sslData.certificate?.hostnameMatch || false
      },

      // Protocol support
      protocols: {
        tls13: sslData.protocols?.tls13 || false,
        tls12: sslData.protocols?.tls12 || false,
        tls11: sslData.protocols?.tls11 || false,
        tls10: sslData.protocols?.tls10 || false,
        ssl3: sslData.protocols?.ssl3 || false
      },

      // Security info
      vulnerabilities: {
        hasVulnerabilities: sslData.vulnerabilities?.hasVulnerabilities || false,
        poodle: sslData.vulnerabilities?.poodle || false,
        freak: sslData.vulnerabilities?.freak || false,
        details: sslData.vulnerabilities || {}
      },

      // Grades breakdown
      grades: sslData.grades || {},

      // Recommendations
      recommendations: sslData.recommendations || [],

      // Error handling
      error: sslData.error,

      // Alert logic
      alert: false,
      alertLevel: 'info',
      alertReasons: []
    };

    // Determine if this needs an alert
    const alertReasons = [];
    let alertLevel = 'info';

    // Critical alerts
    if (sslData.error) {
      alertReasons.push(`Scan failed: ${sslData.error}`);
      alertLevel = 'critical';
    }

    if (!parsedResult.certificate.isValid) {
      alertReasons.push('Certificate is invalid');
      alertLevel = 'critical';
    }

    if (parsedResult.certificate.daysUntilExpiry <= 0) {
      alertReasons.push('Certificate has expired');
      alertLevel = 'critical';
    }

    // High priority alerts
    if (parsedResult.certificate.daysUntilExpiry <= 7 && parsedResult.certificate.daysUntilExpiry > 0) {
      alertReasons.push(`Certificate expires in ${parsedResult.certificate.daysUntilExpiry} days`);
      if (alertLevel === 'info') alertLevel = 'high';
    }

    if (parsedResult.vulnerabilities.hasVulnerabilities) {
      alertReasons.push('Security vulnerabilities detected');
      if (alertLevel === 'info') alertLevel = 'high';
    }

    if (!parsedResult.certificate.hostnameMatch) {
      alertReasons.push('Hostname mismatch detected');
      if (alertLevel === 'info') alertLevel = 'high';
    }

    // Medium priority alerts
    if (parsedResult.certificate.daysUntilExpiry <= 30 && parsedResult.certificate.daysUntilExpiry > 7) {
      alertReasons.push(`Certificate expires in ${parsedResult.certificate.daysUntilExpiry} days`);
      if (alertLevel === 'info') alertLevel = 'medium';
    }

    if (['C', 'D', 'F'].includes(parsedResult.overallGrade)) {
      alertReasons.push(`Poor SSL grade: ${parsedResult.overallGrade}`);
      if (alertLevel === 'info') alertLevel = 'medium';
    }

    if (parsedResult.protocols.ssl3 || parsedResult.protocols.tls10) {
      alertReasons.push('Deprecated protocols enabled');
      if (alertLevel === 'info') alertLevel = 'medium';
    }

    // Low priority alerts
    if (parsedResult.recommendations && parsedResult.recommendations.length > 0) {
      alertReasons.push(`${parsedResult.recommendations.length} recommendations available`);
      if (alertLevel === 'info') alertLevel = 'low';
    }

    // Set alert status
    parsedResult.alert = alertReasons.length > 0;
    parsedResult.alertLevel = alertLevel;
    parsedResult.alertReasons = alertReasons;

    // Add Discord formatting helpers
    parsedResult.discord = {
      color: getDiscordColor(alertLevel, parsedResult.overallGrade),
      title: `SSL Health Check: ${parsedResult.hostname}`,
      description: getDiscordDescription(parsedResult),
      fields: getDiscordFields(parsedResult),
      timestamp: new Date().toISOString(),

      // Pre-formatted content for easy Discord input
      content: alertLevel === 'critical' ? '@here ðŸš¨ SSL Health Monitor Alert' : 'ðŸ” SSL Health Monitor Alert',

      // Simple descriptions without complex logic
      simpleDescription: parsedResult.alert 
        ? `âš ï¸ SSL issues detected for ${parsedResult.hostname}`
        : `âœ… SSL looks healthy for ${parsedResult.hostname}`,

      // Detailed info as simple strings
      certificateInfo: `Grade: ${parsedResult.overallGrade} | Expires in ${parsedResult.certificate.daysUntilExpiry} days`,

      // Alert details as simple string
      alertSummary: parsedResult.alert 
        ? `Issues: ${parsedResult.alertReasons.join(', ')}`
        : `Certificate expires ${parsedResult.certificate.validUntil}`,

      // Complete formatted description
      fullDescription: getFullDiscordDescription(parsedResult)
    };

    results.push({ json: parsedResult });

  } catch (error) {
    // Handle any unexpected errors
    results.push({
      json: {
        hostname: 'unknown',
        status: 'error',
        error: `Processing error: ${error.message}`,
        alert: true,
        alertLevel: 'critical'
      }
    });
  }
}

// Helper functions for Discord formatting
function getDiscordColor(alertLevel, grade) {
  // Color mapping for Discord embeds
  const colors = {
    critical: 15158332, // Red
    high: 15105570,     // Orange
    medium: 15844367,   // Yellow
    low: 5763719,       // Blue
    info: 5763719       // Blue
  };

  // Grade-based colors for non-alerts
  const gradeColors = {
    'A+': 5763719,  // Green
    'A': 3066993,   // Green
    'B': 15844367,  // Yellow
    'C': 15105570,  // Orange
    'D': 15158332,  // Red
    'F': 10038562   // Dark Red
  };

  if (alertLevel !== 'info') {
    return colors[alertLevel] || colors.info;
  }

  return gradeColors[grade] || colors.info;
}

function getFullDiscordDescription(result) {
  if (result.error) {
    return `âŒ SSL scan failed: ${result.error}`;
  }

  if (result.alert) {
    let description = `âš ï¸ SSL issues detected for ${result.hostname}\n`;
    description += `Grade: ${result.overallGrade} | Expires in ${result.certificate.daysUntilExpiry} days\n\n`;
    description += `Issues found:\n`;
    result.alertReasons.forEach(reason => {
      description += `â€¢ ${reason}\n`;
    });
    return description.trim();
  }

  return `âœ… SSL configuration looks good for ${result.hostname}\nGrade: ${result.overallGrade} | Certificate expires in ${result.certificate.daysUntilExpiry} days`;
}

function getDiscordDescription(result) {
  if (result.error) {
    return `âŒ SSL scan failed: ${result.error}`;
  }

  if (result.alert) {
    // Create simple alert description
    let description = "âš ï¸ SSL issues detected:\n";
    result.alertReasons.forEach(reason => {
      description += `â€¢ ${reason}\n`;
    });
    return description.trim();
  }

  return `âœ… SSL configuration looks good\nGrade: ${result.overallGrade} | Expires in ${result.certificate.daysUntilExpiry} days`;
}

function getDiscordFields(result) {
  const fields = [];

  // Certificate info
  fields.push({
    name: "ðŸ“œ Certificate",
    value: [
      `**Subject:** ${result.certificate.subject}`,
      `**Issuer:** ${result.certificate.issuer}`,
      `**Expires:** ${new Date(result.certificate.validUntil).toLocaleDateString()}`,
      `**Days Left:** ${result.certificate.daysUntilExpiry}`
    ].join('\n'),
    inline: true
  });

  // Security info
  fields.push({
    name: "ðŸ” Security",
    value: [
      `**Overall Grade:** ${result.overallGrade}`,
      `**TLS 1.2:** ${result.protocols.tls12 ? 'âœ…' : 'âŒ'}`,
      `**TLS 1.3:** ${result.protocols.tls13 ? 'âœ…' : 'âŒ'}`,
      `**Vulnerabilities:** ${result.vulnerabilities.hasVulnerabilities ? 'âš ï¸ Found' : 'âœ… None'}`
    ].join('\n'),
    inline: true
  });

  // Add recommendations if any
  if (result.recommendations.length > 0) {
    fields.push({
      name: "ðŸ’¡ Recommendations",
      value: result.recommendations.slice(0, 3).map(rec => `â€¢ ${rec.message || rec}`).join('\n') + 
             (result.recommendations.length > 3 ? `\n... and ${result.recommendations.length - 3} more` : ''),
      inline: false
    });
  }

  return fields;
}

return results;
